constraint.script:
"try {
    def method = MethodCall.resolveMethod()
    def clazz = method.getContainingClass()
    if(!clazz.getSupers().find {it.getQualifiedName().equals("controllers.AbstractController")}){
        return false
    }
    def className = clazz.getQualifiedName()
    def returnType = method.getReturnType().getCanonicalText()
    return className.startsWith("controllers.") &&
            method.hasModifierProperty("public") &&
            method.hasModifierProperty("static") &&
            returnType.equals("void") &&
            !method.hasAnnotation("play.mvc.Util")
} catch(e) {
    e.printStackTrace()
    return false
}"
